CREATE TABLE IF NOT EXISTS public.games
(
    id character varying COLLATE pg_catalog."default" NOT NULL,
    name character varying COLLATE pg_catalog."default" NOT NULL,
    created_at timestamp with time zone NOT NULL DEFAULT now(),
    CONSTRAINT games_pkey PRIMARY KEY (id)
)

TABLESPACE pg_default;

ALTER TABLE IF EXISTS public.games
    OWNER to postgres;

GRANT ALL ON TABLE public.games TO anon;

GRANT ALL ON TABLE public.games TO authenticated;

GRANT ALL ON TABLE public.games TO postgres;

GRANT ALL ON TABLE public.games TO service_role;

COMMENT ON TABLE public.games
    IS 'List of games available on Explorers Club';

CREATE TABLE IF NOT EXISTS public.game_instances
(
    id uuid NOT NULL DEFAULT uuid_generate_v4(),
    host_user_id uuid NOT NULL,
    game_id character varying COLLATE pg_catalog."default",
    starting_at timestamp with time zone,
    started_at timestamp with time zone,
    created_at timestamp with time zone DEFAULT now(),
    is_listable boolean NOT NULL DEFAULT true,
    CONSTRAINT game_instances_pkey PRIMARY KEY (id),
    CONSTRAINT game_instances_game_id_fkey FOREIGN KEY (game_id)
        REFERENCES public.games (id) MATCH SIMPLE
        ON UPDATE NO ACTION
        ON DELETE NO ACTION,
    CONSTRAINT game_instances_host_user_id_fkey FOREIGN KEY (host_user_id)
        REFERENCES auth.users (id) MATCH SIMPLE
        ON UPDATE NO ACTION
        ON DELETE NO ACTION
)

TABLESPACE pg_default;

ALTER TABLE IF EXISTS public.game_instances
    OWNER to postgres;

ALTER TABLE IF EXISTS public.game_instances
    ENABLE ROW LEVEL SECURITY;

GRANT ALL ON TABLE public.game_instances TO anon;

GRANT ALL ON TABLE public.game_instances TO authenticated;

